-- Description: Generate ordered DDL statements for all file objects in MYLIB 
  -- First create the table to contain the list of objects. 
 CREATE TABLE QTEMP.INORDER (OBJECT_SCHEMA VARCHAR(258),  OBJECT_NAME VARCHAR(258),  SQL_OBJECT_TYPE CHAR(10));
 -- Now populate the table with objects from a schema. Omit system objects. 
 INSERT INTO qtemp.inorder(object_schema, object_name,     sql_object_type)   SELECT 'MYLIB', dbxfil,     CASE       WHEN dbxatr = 'AL' THEN 'ALIAS'       WHEN dbxatr = 'IX' THEN 'INDEX'       WHEN dbxatr IN ('TB', 'MQ', 'PF') THEN 'TABLE'       WHEN dbxatr IN ('VW', 'LF') THEN 'VIEW'        ELSE dbxatr     END   FROM qsys.qadbxfil f   WHERE dbxlb2 = 'MYLIB' AND         dbxatr IN ('AL','IX','TB','MQ','PF','VW','LF') AND         dbxrel = 'Y' AND         dbxlfi NOT LIKE 'QIDCT%' AND         dbxlfi NOT IN ('SYSCHKCST', 'SYSCOLUMNS', 'SYSCST', 'SYSCSTCOL',        'SYSCSTDEP', 'SYSFIELDS', 'SYSINDEXES', 'SYSKEYCST', 'SYSKEYS',        'SYSPACKAGE', 'SYSREFCST', 'SYSTABLEDEP', 'SYSTABLES', 'SYSTRIGCOL',        'SYSTRIGDEP', 'SYSTRIGGERS', 'SYSTRIGUPD', 'SYSVIEWDEP', 'SYSVIEWS'); 
 -- Generate the ordered DDL 
 CALL QSYS2.GENERATE_SQL_OBJECTS(SYSTEM_TABLE_NAME=>'INORDER',  SYSTEM_TABLE_SCHEMA=>'QTEMP',  QUALIFIED_NAME_OPTION=>'1');

